mapper.select:
SELECT boardno, title, content, memberno, regdate, type, deleted
FROM Board WHERE deleted='0' and type='Notice' ORDER BY boardno desc
LIMIT #{start}, #{count}

mapper.update:
UPDATE Board
SET deleted = '1'
WHERE boardNo = #{ boardNo }

UPDATE Board 
SET title = #{title}, content = #{content}  
WHERE boardno = #{boardNo}

mapper.select:
